  description: string;
  type: "text" | "textarea" | "url" | "video";
  placeholder: string;
}

const CONFIG_FIELDS: ConfigField[] = [
  { key: "manager_chat_id", label: "Chat ID менеджера", description: "Telegram Chat ID менеджера для отримання сповіщень", type: "text", placeholder: "123456789" },
